INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 1, 'Los hijos de Judá: Fares, Hezrón, Carmi, Hur y Sobal.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 2, 'Reaía hijo de Sobal engendró a Jahat, y Jahat engendró a Ahumai y a Lahad. Estas son las familias de los zoratitas.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 3, 'Y estas son las del padre de Etam: Jezreel, Isma e Ibdas. Y el nombre de su hermana fue Haze-lelponi.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 4, 'Penuel fue padre de Gedor, y Ezer padre de Husa. Estos fueron los hijos de Hur primogénito de Efrata, padre de Belén.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 5, 'Asur padre de Tecoa tuvo dos mujeres, Hela y Naara.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 6, 'Y Naara dio a luz a Ahuzam, Hefer, Temeni y Ahastari. Estos fueron los hijos de Naara.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 7, 'Los hijos de Hela: Zeret, Jezoar y Etnán.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 8, 'Cos engendró a Anub, a Zobeba, y la familia de Aharhel hijo de Harum.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 9, 'Y Jabes fue más ilustre que sus hermanos, al cual su madre llamó Jabes, diciendo: Por cuanto lo di a luz en dolor.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 10, 'E invocó Jabes al Dios de Israel, diciendo: ¡Oh, si me dieras bendición, y ensancharas mi territorio, y si tu mano estuviera conmigo, y me libraras de mal, para que no me dañe! Y le otorgó Dios lo que pidió.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 11, 'Quelub hermano de Súa engendró a Mehir, el cual fue padre de Estón.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 12, 'Y Estón engendró a Bet-rafa, a Paseah, y a Tehina padre de la ciudad de Nahas; éstos son los varones de Reca.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 13, 'Los hijos de Cenaz: Otoniel y Seraías. Los hijos de Otoniel: Hatat,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 14, 'y Meonotai, el cual engendró a Ofra. Y Seraías engendró a Joab, padre de los habitantes del valle de Carisim, porque fueron artífices.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 15, 'Los hijos de Caleb hijo de Jefone: Iru, Ela y Naam; e hijo de Ela fue Cenaz.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 16, 'Los hijos de Jehalelel: Zif, Zifa, Tirías y Asareel.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 17, 'Y los hijos de Esdras: Jeter, Mered, Efer y Jalón; también engendró a María, a Samai y a Isba padre de Estemoa.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 18, 'Y su mujer Jehudaía dio a luz a Jered padre de Gedor, a Heber padre de Soco y a Jecutiel padre de Zanoa. Estos fueron los hijos de Bitia hija de Faraón, con la cual casó Mered.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 19, 'Y los hijos de la mujer de Hodías, hermana de Naham, fueron el padre de Keila garmita, y Estemoa maacateo.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 20, 'Los hijos de Simón: Amnón, Rina, Ben-hanán y Tilón. Y los hijos de Isi: Zohet y Benzohet.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 21, 'Los hijos de Sela hijo de Judá: Er padre de Leca, y Laada padre de Maresa, y las familias de los que trabajan lino en Bet- asbea;');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 22, 'y Joacim, y los varones de Cozeba, Joás, y Saraf, los cuales dominaron en Moab y volvieron a Lehem, según registros antiguos.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 23, 'Estos eran alfareros, y moraban en medio de plantíos y cercados; moraban allá con el rey, ocupados en su servicio.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 24, 'Los hijos de Simeón: Nemuel, Jamín, Jarib, Zera, Saúl,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 25, 'y Salum su hijo, Mibsam su hijo y Misma su hijo.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 26, 'Los hijos de Misma: Hamuel su hijo, Zacur su hijo, y Simei su hijo.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 27, 'Los hijos de Simei fueron dieciséis, y seis hijas; pero sus hermanos no tuvieron muchos hijos, ni multiplicaron toda su familia como los hijos de Judá.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 28, 'Y habitaron en Beerseba, Molada, Hazar-sual,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 29, 'Bilha, Ezem, Tolad,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 30, 'Betuel, Horma, Siclag,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 31, 'Bet-marcabot, Hazar-susim, Bet-birai y Saaraim. Estas fueron sus ciudades hasta el reinado de David.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 32, 'Y sus aldeas fueron Etam, Aín, Rimón, Toquén y Asán; cinco pueblos,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 33, 'y todas sus aldeas que estaban en contorno de estas ciudades hasta Baal. Esta fue su habitación, y esta su descendencia.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 34, 'Y Mesobab, Jamlec, Josías hijo de Amasías,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 35, 'Joel, Jehú hijo de Josibías, hijo de Seraías, hijo de Asiel,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 36, 'Elioenai, Jaacoba, Jesohaía, Asaías, Adiel, Jesimiel, Benaía,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 37, 'y Ziza hijo de Sifi, hijo de Alón, hijo de Jedaías, hijo de Simri, hijo de Semaías.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 38, 'Estos, por sus nombres, son los principales entre sus familias; y las casas de sus padres fueron multiplicadas en gran manera.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 39, 'Y llegaron hasta la entrada de Gedor hasta el oriente del valle, buscando pastos para sus ganados.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 40, 'Y hallaron gruesos y buenos pastos, y tierra ancha y espaciosa, quieta y reposada, porque los de Cam la habitaban antes.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 41, 'Y estos que han sido escritos por sus nombres, vinieron en días de Ezequías rey de Judá, y desbarataron sus tiendas y cabañas que allí hallaron, y los destruyeron hasta hoy, y habitaron allí en lugar de ellos; por cuanto había allí pastos para sus ganados.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 42, 'Asimismo quinientos hombres de ellos, de los hijos de Simeón, fueron al monte de Seir, llevando por capitanes a Pelatías, Nearías, Refaías y Uziel, hijos de Isi,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 4, 43, 'y destruyeron a los que habían quedado de Amalec, y habitaron allí hasta hoy.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 1, 'Los hijos de Rubén primogénito de Israel (porque él era el primogénito, mas como violó el lecho de su padre, sus derechos de primogenitura fueron dados a los hijos de José, hijo de Israel, y no fue contado por primogénito;');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 2, 'bien que Judá llegó a ser el mayor sobre sus hermanos, y el príncipe de ellos; mas el derecho de primogenitura fue de José);');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 3, 'fueron, pues, los hijos de Rubén primogénito de Israel: Hanoc, Falú, Hezrón y Carmi.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 4, 'Los hijos de Joel: Semaías su hijo, Gog su hijo, Simei su hijo,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 5, 'Micaía su hijo, Reaía su hijo, Baal su hijo,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 6, 'Beera su hijo, el cual fue transportado por Tiglat-pileser rey de los asirios. Este era principal de los rubenitas.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 7, 'Y sus hermanos por sus familias, cuando eran contados en sus descendencias, tenían por príncipes a Jeiel y a Zacarías.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 8, 'Y Bela hijo de Azaz, hijo de Sema, hijo de Joel, habitó en Aroer hasta Nebo y Baal-meón.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 9, 'Habitó también desde el oriente hasta la entrada del desierto, desde el río Eufrates; porque tenía mucho ganado en la tierra de Galaad.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 10, 'Y en los días de Saúl hicieron guerra contra los agarenos, los cuales cayeron en su mano; y ellos habitaron en sus tiendas en toda la región oriental de Galaad.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 11, 'Y los hijos de Gad habitaron enfrente de ellos en la tierra de Basán hasta Salca.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 12, 'Joel fue el principal en Basán; el segundo Safán, luego Jaanai, después Safat.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 13, 'Y sus hermanos, según las familias de sus padres, fueron Micael, Mesulam, Seba, Jorai, Jacán, Zía y Heber; por todos siete.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 14, 'Estos fueron los hijos de Abihail hijo de Huri, hijo de Jaroa, hijo de Galaad, hijo de Micael, hijo de Jesisai, hijo de Jahdo, hijo de Buz.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 15, 'También Ahí hijo de Abdiel, hijo de Guni, fue principal en la casa de sus padres.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 16, 'Y habitaron en Galaad, en Basán y en sus aldeas, y en todos los ejidos de Sarón hasta salir de ellos.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 17, 'Todos éstos fueron contados por sus generaciones en días de Jotam rey de Judá y en días de Jeroboam rey de Israel.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 18, 'Los hijos de Rubén y de Gad, y la media tribu de Manasés, hombres valientes, hombres que traían escudo y espada, que entesaban arco, y diestros en la guerra, eran cuarenta y cuatro mil setecientos sesenta que salían a batalla.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 19, 'Estos tuvieron guerra contra los agarenos, y Jetur, Nafis y Nodab.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 20, 'Y fueron ayudados contra ellos, y los agarenos y todos los que con ellos estaban se rindieron en sus manos; porque clamaron a Dios en la guerra, y les fue favorable, porque esperaron en él.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 21, 'Y tomaron sus ganados, cincuenta mil camellos, doscientas cincuenta mil ovejas y dos mil asnos; y cien mil personas.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 22, 'Y cayeron muchos muertos, porque la guerra era de Dios; y habitaron en sus lugares hasta el cautiverio.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 23, 'Los hijos de la media tribu de Manasés, multiplicados en gran manera, habitaron en la tierra desde Basán hasta Baal- hermón y Senir y el monte de Hermón.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 24, 'Y estos fueron los jefes de las casas de sus padres: Efer, Isi, Eliel, Azriel, Jeremías, Hodavías y Jahdiel, hombres valientes y esforzados, varones de nombre y jefes de las casas de sus padres.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 25, 'Pero se rebelaron contra el Dios de sus padres, y se prostituyeron siguiendo a los dioses de los pueblos de la tierra, a los cuales Jehová había quitado de delante de ellos;');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 5, 26, 'por lo cual el Dios de Israel excitó el espíritu de Pul rey de los asirios, y el espíritu de Tiglat-pileser rey de los asirios, el cual transportó a los rubenitas y gaditas y a la media tribu de Manasés, y los llevó a Halah, a Habor, a Hara y al río Gozán, hasta hoy.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 1, 'Los hijos de Leví: Gersón, Coat y Merari.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 2, 'Los hijos de Coat: Amram, Izhar, Hebrón y Uziel.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 3, 'Los hijos de Amram: Aarón, Moisés y María. Los hijos de Aarón: Nadab, Abiú, Eleazar e Itamar.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 4, 'Eleazar engendró a Finees, Finees engendró a Abisúa,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 5, 'Abisúa engendró a Buqui, Buqui engendró a Uzi,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 6, 'Uzi engendró a Zeraías, Zeraías engendró a Meraiot,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 7, 'Meraiot engendró a Amarías, Amarías engendró a Ahitob,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 8, 'Ahitob engendró a Sadoc, Sadoc engendró a Ahimaas,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 9, 'Ahimaas engendró a Azarías, Azarías engendró a Johanán,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 10, 'y Johanán engendró a Azarías, el que tuvo el sacerdocio en la casa que Salomón edificó en Jerusalén.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 11, 'Azarías engendró a Amarías, Amarías engendró a Ahitob,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 12, 'Ahitob engendró a Sadoc, Sadoc engendró a Salum,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 13, 'Salum engendró a Hilcías, Hilcías engendró a Azarías,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 14, 'Azarías engendró a Seraías, y Seraías engendró a Josadac,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 15, 'y Josadac fue llevado cautivo cuando Jehová transportó a Judá y a Jerusalén por mano de Nabucodonosor.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 16, 'Los hijos de Leví: Gersón, Coat y Merari.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 17, 'Y estos son los nombres de los hijos de Gersón: Libni y Simei.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 18, 'Los hijos de Coat: Amram, Izhar, Hebrón y Uziel.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 19, 'Los hijos de Merari: Mahli y Musi. Estas son las familias de Leví, según sus descendencias.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 20, 'Gersón: Libni su hijo, Jahat su hijo, Zima su hijo,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 21, 'Joa su hijo, Iddo su hijo, Zera su hijo, Jeatrai su hijo.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 22, 'Los hijos de Coat: Aminadab su hijo, Coré su hijo, Asir su hijo,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 23, 'Elcana su hijo, Ebiasaf su hijo, Asir su hijo,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 24, 'Tahat su hijo, Uriel su hijo, Uzías su hijo, y Saúl su hijo.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 25, 'Los hijos de Elcana: Amasai y Ahimot;');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 26, 'Elcana su hijo, Zofai su hijo, Nahat su hijo.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 27, 'Eliab su hijo, Jeroham su hijo, Elcana su hijo.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 28, 'Los hijos de Samuel: el primogénito Vasni, y Abías.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 29, 'Los hijos de Merari: Mahli, Libni su hijo, Simei su hijo, Uza su hijo,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 30, 'Simea su hijo, Haguía su hijo, Asaías su hijo.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 31, 'Estos son los que David puso sobre el servicio de canto en la casa de Jehová, después que el arca tuvo reposo,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 32, 'los cuales servían delante de la tienda del tabernáculo de reunión en el canto, hasta que Salomón edificó la casa de Jehová en Jerusalén; después estuvieron en su ministerio según su costumbre.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 33, 'Estos, pues, con sus hijos, ayudaban: de los hijos de Coat, el cantor Hemán hijo de Joel, hijo de Samuel,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 34, 'hijo de Elcana, hijo de Jeroham, hijo de Eliel, hijo de Toa,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 35, 'hijo de Zuf, hijo de Elcana, hijo de Mahat, hijo de Amasai,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 36, 'hijo de Elcana, hijo de Joel, hijo de Azarías, hijo de Sofonías,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 37, 'hijo de Tahat, hijo de Asir, hijo de Ebiasaf, hijo de Coré,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 38, 'hijo de Izhar, hijo de Coat, hijo de Leví, hijo de Israel;');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 39, 'y su hermano Asaf, el cual estaba a su mano derecha; Asaf, hijo de Berequías, hijo de Simea,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 40, 'hijo de Micael, hijo de Baasías, hijo de Malquías,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 41, 'hijo de Etni, hijo de Zera, hijo de Adaía,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 42, 'hijo de Etán, hijo de Zima, hijo de Simei,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 43, 'hijo de Jahat, hijo de Gersón, hijo de Leví.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 44, 'Pero a la mano izquierda estaban sus hermanos los hijos de Merari, esto es, Etán hijo de Quisi, hijo de Abdi, hijo de Maluc,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 45, 'hijo de Hasabías, hijo de Amasías, hijo de Hilcías,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 46, 'hijo de Amsi, hijo de Bani, hijo de Semer,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 47, 'hijo de Mahli, hijo de Musi, hijo de Merari, hijo de Leví.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 48, 'Y sus hermanos los levitas fueron puestos sobre todo el ministerio del tabernáculo de la casa de Dios.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 49, 'Mas Aarón y sus hijos ofrecían sacrificios sobre el altar del holocausto, y sobre el altar del perfume quemaban incienso, y ministraban en toda la obra del lugar santísimo, y hacían las expiaciones por Israel conforme a todo lo que Moisés siervo de Dios había mandado.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 50, 'Los hijos de Aarón son estos: Eleazar su hijo, Finees su hijo, Abisúa su hijo,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 51, 'Buqui su hijo, Uzi su hijo, Zeraías su hijo,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 52, 'Meraiot su hijo, Amarías su hijo, Ahitob su hijo,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 53, 'Sadoc su hijo, Ahimaas su hijo.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 54, 'Estas son sus habitaciones, conforme a sus domicilios y sus términos, las de los hijos de Aarón por las familias de los coatitas, porque a ellos les tocó en suerte.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 55, 'Les dieron, pues, Hebrón en tierra de Judá, y sus ejidos alrededor de ella.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 56, 'Pero el territorio de la ciudad y sus aldeas se dieron a Caleb, hijo de Jefone.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 57, 'De Judá dieron a los hijos de Aarón la ciudad de refugio, esto es, Hebrón; además, Libna con sus ejidos, Jatir, Estemoa con sus ejidos,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 58, 'Hilén con sus ejidos, Debir con sus ejidos,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 59, 'Asán con sus ejidos y Bet-semes con sus ejidos.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 60, 'Y de la tribu de Benjamín, Geba con sus ejidos, Alemet con sus ejidos y Anatot con sus ejidos. Todas sus ciudades fueron trece ciudades, repartidas por sus linajes.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 61, 'A los hijos de Coat que quedaron de su parentela, dieron por suerte diez ciudades de la media tribu de Manasés.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 62, 'A los hijos de Gersón, por sus linajes, dieron de la tribu de Isacar, de la tribu de Aser, de la tribu de Neftalí y de la tribu de Manasés en Basán, trece ciudades.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 63, 'Y a los hijos de Merari, por sus linajes, de la tribu de Rubén, de la tribu de Gad y de la tribu de Zabulón, dieron por suerte doce ciudades.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 64, 'Y los hijos de Israel dieron a los levitas ciudades con sus ejidos.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 65, 'Dieron por suerte de la tribu de los hijos de Judá, de la tribu de los hijos de Simeón y de la tribu de los hijos de Benjamín, las ciudades que nombraron por sus nombres.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 66, 'A las familias de los hijos de Coat dieron ciudades con sus ejidos de la tribu de Efraín.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 67, 'Les dieron la ciudad de refugio, Siquem con sus ejidos en el monte de Efraín; además, Gezer con sus ejidos,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 68, 'Jocmeam con sus ejidos, Bet-horón con sus ejidos,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 69, 'Ajalón con sus ejidos y Gat-rimón con sus ejidos.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 70, 'De la media tribu de Manasés, Aner con sus ejidos y Bileam con sus ejidos, para los de las familias de los hijos de Coat que habían quedado.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 71, 'A los hijos de Gersón dieron de la media tribu de Manasés, Golán en Basán con sus ejidos y Astarot con sus ejidos.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 72, 'De la tribu de Isacar, Cedes con sus ejidos, Daberat con sus ejidos,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 73, 'Ramot con sus ejidos y Anem con sus ejidos.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 74, 'De la tribu de Aser, Masal con sus ejidos, Abdón con sus ejidos,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 75, 'Hucoc con sus ejidos y Rehob con sus ejidos.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 76, 'De la tribu de Neftalí, Cedes en Galilea con sus ejidos, Hamón con sus ejidos y Quiriataim con sus ejidos.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 77, 'A los hijos de Merari que habían quedado, dieron de la tribu de Zabulón, Rimón con sus ejidos y Tabor con sus ejidos.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 78, 'Del otro lado del Jordán frente a Jericó, al oriente del Jordán, dieron de la tribu de Rubén, Beser en el desierto con sus ejidos, Jaza con sus ejidos,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 79, 'Cademot con sus ejidos y Mefaat con sus ejidos.');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 80, 'Y de la tribu de Gad, Ramot de Galaad con sus ejidos, Mahanaim con sus ejidos,');
INSERT INTO Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 13, 6, 81, 'Hesbón con sus ejidos y Jazer con sus ejidos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 1	, 'Cuando el rey David era viejo y avanzado en días, le cubrían de ropas, pero no se calentaba. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 2	, 'Le dijeron, por tanto, sus siervos: Busquen para mi señor el rey una joven virgen, para que esté delante del rey y lo abrigue, y duerma a su lado, y entrará en calor mi señor el rey.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 3	, 'Y buscaron una joven hermosa por toda la tierra de Israel, y hallaron a Abisag sunamita, y la trajeron al rey. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 4	, 'Y la joven era hermosa; y ella abrigaba al rey, y le servía; pero el rey nunca la conoció. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 5	, 'Adonías usurpa el trono. Entonces Adonías hijo de Haguit se rebeló, diciendo: Yo reinaré. Y se hizo de carros y de gente de a caballo, y de cincuenta hombres que corriesen delante de él.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 6	, 'Y su padre nunca le había entristecido en todos sus días con decirle: ¿Por qué haces así? Además, éste era de muy hermoso parecer; y había nacido después de Absalón. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 7	, 'Y se había puesto de acuerdo con Joab hijo de Sarvia y con el sacerdote Abiatar, los cuales ayudaban a Adonías.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 8	, 'Pero el sacerdote Sadoc, y Benaía hijo de Joiada, el profeta Natán, Simei, Rei y todos los grandes de David, no seguían a Adonías. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 9	, 'Y matando Adonías ovejas y vacas y animales gordos junto a la peña de Zohelet, la cual está cerca de la fuente de Rogel, convidó a todos sus hermanos los hijos del rey, y a todos los varones de Judá, siervos del rey;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 10, 'Pero no convidó al profeta Natán, ni a Benaía, ni a los grandes, ni a Salomón su hermano. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 11, 'Entonces habló Natán a Betsabé madre de Salomón, diciendo: ¿No has oído que reina Adonías hijo de Haguit, sin saberlo David nuestro señor?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 12, 'Ven pues, ahora, y toma mi consejo, para que conserves tu vida, y la de tu hijo Salomón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 13, 'Ve y entra al rey David, y dile: Rey señor mío, ¿no juraste a tu sierva, diciendo: Salomón tu hijo reinará después de mí, y él se sentará en mi trono? ¿Por qué, pues, reina Adonías?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 14, 'Y estando tú aún hablando con el rey, yo entraré tras ti y reafirmaré tus razones. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 15, 'Entonces Betsabé entró a la cámara del rey; y el rey era muy viejo, y Abisag sunamita le servía.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 16, 'Y Betsabé se inclinó, e hizo reverencia al rey. Y el rey dijo: ¿Qué tienes?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 17, 'Y ella le respondió: Señor mío, tú juraste a tu sierva por Jehová tu Dios, diciendo: Salomón tu hijo reinará después de mí, y él se sentará en mi trono.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 18, 'Y he aquí ahora Adonías reina, y tú, mi señor rey, hasta ahora no lo sabes.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 19, 'Ha matado bueyes, y animales gordos, y muchas ovejas, y ha convidado a todos los hijos del rey, al sacerdote Abiatar, y a Joab general del ejército; mas a Salomón tu siervo no ha convidado.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 20, 'Entre tanto, rey señor mío, los ojos de todo Israel están puestos en ti, para que les declares quién se ha de sentar en el trono de mi señor el rey después de él.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 21, 'De otra manera sucederá que cuando mi señor el rey duerma con sus padres, yo y mi hijo Salomón seremos tenidos por culpables. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 22, 'Mientras aún hablaba ella con el rey, he aquí vino el profeta Natán.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 23, 'Y dieron aviso al rey, diciendo: He aquí el profeta Natán; el cual, cuando entró al rey, se postró delante del rey inclinando su rostro a tierra.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 24, 'Y dijo Natán: Rey señor mío, ¿has dicho tú: Adonías reinará después de mí, y él se sentará en mi trono?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 25, 'Porque hoy ha descendido, y ha matado bueyes y animales gordos y muchas ovejas, y ha convidado a todos los hijos del rey, y a los capitanes del ejército, y también al sacerdote Abiatar; y he aquí, están comiendo y bebiendo delante de él, y han dicho: ¡Viva el rey Adonías!');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 26, 'Pero ni a mí tu siervo, ni al sacerdote Sadoc, ni a Benaía hijo de Joiada, ni a Salomón tu siervo, ha convidado.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 27, '¿Es este negocio ordenado por mi señor el rey, sin haber declarado a tus siervos quién se había de sentar en el trono de mi señor el rey después de él?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 28, 'David proclama rey a Salomón. Entonces el rey David respondió y dijo: Llamadme a Betsabé. Y ella entró a la presencia del rey, y se puso delante del rey.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 29, 'Y el rey juró diciendo: Vive Jehová, que ha redimido mi alma de toda angustia,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 30, 'que como yo te he jurado por Jehová Dios de Israel, diciendo: Tu hijo Salomón reinará después de mí, y él se sentará en mi trono en lugar mío; que así lo haré hoy.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 31, 'Entonces Betsabé se inclinó ante el rey, con su rostro a tierra, y haciendo reverencia al rey, dijo: Viva mi señor el rey David para siempre.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 32, 'Y el rey David dijo: Llamadme al sacerdote Sadoc, al profeta Natán, y a Benaía hijo de Joiada. Y ellos entraron a la presencia del rey.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 33, 'Y el rey les dijo: Tomad con vosotros los siervos de vuestro señor, y montad a Salomón mi hijo en mi mula, y llevadlo a Gihón;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 34, 'y allí lo ungirán el sacerdote Sadoc y el profeta Natán como rey sobre Israel, y tocaréis trompeta, diciendo: ¡Viva el rey Salomón!');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 35, 'Después iréis vosotros detrás de él, y vendrá y se sentará en mi trono, y él reinará por mí; porque a él he escogido para que sea príncipe sobre Israel y sobre Judá.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 36, 'Entonces Benaía hijo de Joiada respondió al rey y dijo: Amén. Así lo diga Jehová, Dios de mi señor el rey. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 37, 'De la manera que Jehová ha estado con mi señor el rey, así esté con Salomón, y haga mayor su trono que el trono de mi señor el rey David. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 38, 'Y descendieron el sacerdote Sadoc, el profeta Natán, Benaía hijo de Joiada, y los cereteos y los peleteos, y montaron a Salomón en la mula del rey David, y lo llevaron a Gihón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 39, 'Y tomando el sacerdote Sadoc el cuerno del aceite del tabernáculo, ungió a Salomón; y tocaron trompeta, y dijo todo el pueblo: ¡Viva el rey Salomón!');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 40, 'Después subió todo el pueblo en pos de él, y cantaba la gente con flautas, y hacían grandes alegrías, que parecía que la tierra se hundía con el clamor de ellos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 41, 'Y lo oyó Adonías, y todos los convidados que con él estaban, cuando ya habían acabado de comer. Y oyendo Joab el sonido de la trompeta, dijo: ¿Por qué se alborota la ciudad con estruendo?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 42, 'Mientras él aún hablaba, he aquí vino Jonatán hijo del sacerdote Abiatar, al cual dijo Adonías: Entra, porque tú eres hombre valiente, y traerás buenas nuevas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 43, 'Jonatán respondió y dijo a Adonías: Ciertamente nuestro señor el rey David ha hecho rey a Salomón;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 44, 'y el rey ha enviado con él al sacerdote Sadoc y al profeta Natán, y a Benaía hijo de Joiada, y también a los cereteos y a los peleteos, los cuales le montaron en la mula del rey;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 45, 'y el sacerdote Sadoc y el profeta Natán lo han ungido por rey en Gihón, y de allí han subido con alegrías, y la ciudad está llena de estruendo. Este es el alboroto que habéis oído.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 46, 'También Salomón se ha sentado en el trono del reino, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 47, 'y aun los siervos del rey han venido a bendecir a nuestro señor el rey David, diciendo: Dios haga bueno el nombre de Salomón más que tu nombre, y haga mayor su trono que el tuyo. Y el rey adoró en la cama.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 48, 'Además el rey ha dicho así: Bendito sea Jehová Dios de Israel, que ha dado hoy quien se siente en mi trono, viéndolo mis ojos. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 49, 'Ellos entonces se estremecieron, y se levantaron todos los convidados que estaban con Adonías, y se fue cada uno por su camino.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 50, 'Mas Adonías, temiendo de la presencia de Salomón, se levantó y se fue, y se asió de los cuernos del altar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 51, 'Y se lo hicieron saber a Salomón, diciendo: He aquí que Adonías tiene miedo del rey Salomón, pues se ha asido de los cuernos del altar, diciendo: Júreme hoy el rey Salomón que no matará a espada a su siervo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 52, 'Y Salomón dijo: Si él fuere hombre de bien, ni uno de sus cabellos caerá en tierra; mas si se hallare mal en él, morirá.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 1, 53, 'Y envió el rey Salomón, y lo trajeron del altar; y él vino, y se inclinó ante el rey Salomón. Y Salomón le dijo: Vete a tu casa.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 1	, 'Mandato de David a Salomón. Llegaron los días en que David había de morir, y ordenó a Salomón su hijo, diciendo:');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 2	, 'Yo sigo el camino de todos en la tierra; esfuérzate, y sé hombre.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 3	, 'Guarda los preceptos de Jehová tu Dios, andando en sus caminos, y observando sus estatutos y mandamientos, sus decretos y sus testimonios, de la manera que está escrito en la ley de Moisés, para que prosperes en todo lo que hagas y en todo aquello que emprendas;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 4	, 'para que confirme Jehová la palabra que me habló, diciendo: Si tus hijos guardaren mi camino, andando delante de mí con verdad, de todo su corazón y de toda su alma, jamás, dice, faltará a ti varón en el trono de Israel.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 5	, 'Ya sabes tú lo que me ha hecho Joab hijo de Sarvia, lo que hizo a dos generales del ejército de Israel, a Abner hijo de Ner y a Amasa hijo de Jeter, a los cuales él mató, derramando en tiempo de paz la sangre de guerra, y poniendo sangre de guerra en el talabarte que tenía sobre sus lomos, y en los zapatos que tenía en sus pies.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 6	, 'Tú, pues, harás conforme a tu sabiduría; no dejarás descender sus canas al Seol en paz.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 7	, 'Mas a los hijos de Barzilai galaadita harás misericordia, que sean de los convidados a tu mesa; porque ellos vinieron de esta manera a mí, cuando iba huyendo de Absalón tu hermano.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 8	, 'También tienes contigo a Simei hijo de Gera, hijo de Benjamín, de Bahurim, el cual me maldijo con una maldición fuerte el día que yo iba a Mahanaim. Mas él mismo descendió a recibirme al Jordán, y yo le juré por Jehová diciendo: Yo no te mataré a espada.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 9	, 'Pero ahora no lo absolverás; pues hombre sabio eres, y sabes cómo debes hacer con él; y harás descender sus canas con sangre al Seol.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 10, 'Muerte de David .(1 Cr. 29. 26-30) Y durmió David con sus padres, y fue sepultado en su ciudad.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 11, 'Los días que reinó David sobre Israel fueron cuarenta años; siete años reinó en Hebrón, y treinta y tres años reinó en Jerusalén.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 12, 'Y se sentó Salomón en el trono de David su padre, y su reino fue firme en gran manera.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 13, 'Salomón afirma su reino. Entonces Adonías hijo de Haguit vino a Betsabé madre de Salomón; y ella le dijo: ¿Es tu venida de paz? El respondió: Sí, de paz.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 14, 'En seguida dijo: Una palabra tengo que decirte. Y ella dijo: Di.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 15, 'El dijo: Tú sabes que el reino era mío, y que todo Israel había puesto en mí su rostro para que yo reinara; mas el reino fue traspasado, y vino a ser de mi hermano, porque por Jehová era suyo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 16, 'Ahora yo te hago una petición; no me la niegues. Y ella le dijo: Habla.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 17, 'El entonces dijo: Yo te ruego que hables al rey Salomón (porque él no te lo negará), para que me dé Abisag sunamita por mujer.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 18, 'Y Betsabé dijo: Bien; yo hablaré por ti al rey.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 19, 'Vino Betsabé al rey Salomón para hablarle por Adonías. Y el rey se levantó a recibirla, y se inclinó ante ella, y volvió a sentarse en su trono, e hizo traer una silla para su madre, la cual se sentó a su diestra.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 20, 'Y ella dijo: Una pequeña petición pretendo de ti; no me la niegues. Y el rey le dijo: Pide, madre mía, que yo no te la negaré.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 21, 'Y ella dijo: Dese Abisag sunamita por mujer a tu hermano Adonías.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 22, 'El rey Salomón respondió y dijo a su madre: ¿Por qué pides a Abisag sunamita para Adonías? Demanda también para él el reino; porque él es mi hermano mayor, y ya tiene también al sacerdote Abiatar, y a Joab hijo de Sarvia.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 23, 'Y el rey Salomón juró por Jehová, diciendo: Así me haga Dios y aun me añada, que contra su vida ha hablado Adonías estas palabras.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 24, 'Ahora, pues, vive Jehová, quien me ha confirmado y me ha puesto sobre el trono de David mi padre, y quien me ha hecho casa, como me había dicho, que Adonías morirá hoy.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 25, 'Entonces el rey Salomón envió por mano de Benaía hijo de Joiada, el cual arremetió contra él, y murió.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 26, 'Y el rey dijo al sacerdote Abiatar: Vete a Anatot, a tus heredades, pues eres digno de muerte; pero no te mataré hoy, por cuanto has llevado el arca de Jehová el Señor delante de David mi padre, y además has sido afligido en todas las cosas en que fue afligido mi padre.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 27, 'Así echó Salomón a Abiatar del sacerdocio de Jehová, para que se cumpliese la palabra de Jehová que había dicho sobre la casa de Elí en Silo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 28, 'Y vino la noticia a Joab; porque también Joab se había adherido a Adonías, si bien no se había adherido a Absalón. Y huyó Joab al tabernáculo de Jehová, y se asió de los cuernos del altar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 29, 'Y se le hizo saber a Salomón que Joab había huido al tabernáculo de Jehová, y que estaba junto al altar. Entonces envió Salomón a Benaía hijo de Joiada, diciendo: Ve, y arremete contra él.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 30, 'Y entró Benaía al tabernáculo de Jehová, y le dijo: El rey ha dicho que salgas. Y él dijo: No, sino que aquí moriré. Y Benaía volvió con esta respuesta al rey, diciendo: Así dijo Joab, y así me respondió.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 31, 'Y el rey le dijo: Haz como él ha dicho; mátale y entiérrale, y quita de mí y de la casa de mi padre la sangre que Joab ha derramado injustamente.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 32, 'Y Jehová hará volver su sangre sobre su cabeza; porque él ha dado muerte a dos varones más justos y mejores que él, a los cuales mató a espada sin que mi padre David supiese nada: a Abner hijo de Ner, general del ejército de Israel, y a Amasa hijo de Jeter, general del ejército de Judá.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 33, 'La sangre, pues, de ellos recaerá sobre la cabeza de Joab, y sobre la cabeza de su descendencia para siempre; mas sobre David y sobre su descendencia, y sobre su casa y sobre su trono, habrá perpetuamente paz de parte de Jehová.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 34, 'Entonces Benaía hijo de Joiada subió y arremetió contra él, y lo mató; y fue sepultado en su casa en el desierto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 35, 'Y el rey puso en su lugar a Benaía hijo de Joiada sobre el ejército, y a Sadoc puso el rey por sacerdote en lugar de Abiatar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 36, 'Después envió el rey e hizo venir a Simei, y le dijo: Edifícate una casa en Jerusalén y mora ahí, y no salgas de allí a una parte ni a otra;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 37, 'porque sabe de cierto que el día que salieres y pasares el torrente de Cedrón, sin duda morirás, y tu sangre será sobre tu cabeza.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 38, 'Y Simei dijo al rey: La palabra es buena; como el rey mi señor ha dicho, así lo hará tu siervo. Y habitó Simei en Jerusalén muchos días. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 39, 'Pero pasados tres años, aconteció que dos siervos de Simei huyeron a Aquis hijo de Maaca, rey de Gat. Y dieron aviso a Simei, diciendo: He aquí que tus siervos están en Gat.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 40, 'Entonces Simei se levantó y ensilló su asno y fue a Aquis en Gat, para buscar a sus siervos. Fue, pues, Simei, y trajo sus siervos de Gat.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 41, 'Luego fue dicho a Salomón que Simei había ido de Jerusalén hasta Gat, y que había vuelto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 42, 'Entonces el rey envió e hizo venir a Simei, y le dijo: ¿No te hice jurar yo por Jehová, y te protesté diciendo: El día que salieres y fueres acá o allá, sabe de cierto que morirás? Y tú me dijiste: La palabra es buena, yo la obedezco.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 43, '¿Por qué, pues, no guardaste el juramento de Jehová, y el mandamiento que yo te impuse?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 44, 'Dijo además el rey a Simei: Tú sabes todo el mal, el cual tu corazón bien sabe, que cometiste contra mi padre David; Jehová, pues, ha hecho volver el mal sobre tu cabeza.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 45, 'Y el rey Salomón será bendito, y el trono de David será firme perpetuamente delante de Jehová.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 2, 46, 'Entonces el rey mandó a Benaía hijo de Joiada, el cual salió y lo hirió, y murió. Y el reino fue confirmado en la mano de Salomón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 1	, 'Salomón se casa con la hija de Faraón. Salomón hizo parentesco con Faraón rey de Egipto, pues tomó la hija de Faraón, y la trajo a la ciudad de David, entre tanto que acababa de edificar su casa, y la casa de Jehová, y los muros de Jerusalén alrededor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 2	, 'Hasta entonces el pueblo sacrificaba en los lugares altos; porque no había casa edificada al nombre de Jehová hasta aquellos tiempos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 3	, 'Salomón pide sabiduría. (2 Cr. 1.1-13) Mas Salomón amó a Jehová, andando en los estatutos de su padre David; solamente sacrificaba y quemaba incienso en los lugares altos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 4	, 'E iba el rey a Gabaón, porque aquél era el lugar alto principal, y sacrificaba allí; mil holocaustos sacrificaba Salomón sobre aquel altar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 5	, 'Y se le apareció Jehová a Salomón en Gabaón una noche en sueños, y le dijo Dios: Pide lo que quieras que yo te dé.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 6	, 'Y Salomón dijo: Tú hiciste gran misericordia a tu siervo David mi padre, porque él anduvo delante de ti en verdad, en justicia, y con rectitud de corazón para contigo; y tú le has reservado esta tu gran misericordia, en que le diste hijo que se sentase en su trono, como sucede en este día.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 7	, 'Ahora pues, Jehová Dios mío, tú me has puesto a mí tu siervo por rey en lugar de David mi padre; y yo soy joven, y no sé cómo entrar ni salir.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 8	, 'Y tu siervo está en medio de tu pueblo al cual tú escogiste; un pueblo grande, que no se puede contar ni numerar por su multitud.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 9	, 'Da, pues, a tu siervo corazón entendido para juzgar a tu pueblo, y para discernir entre lo bueno y lo malo; porque ¿quién podrá gobernar este tu pueblo tan grande?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 10, 'Y agradó delante del Señor que Salomón pidiese esto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 11, 'Y le dijo Dios: Porque has demandado esto, y no pediste para ti muchos días, ni pediste para ti riquezas, ni pediste la vida de tus enemigos, sino que demandaste para ti inteligencia para oir juicio,');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 12, 'he aquí lo he hecho conforme a tus palabras; he aquí que te he dado corazón sabio y entendido, tanto que no ha habido antes de ti otro como tú, ni después de ti se levantará otro como tú.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 13, 'Y aun también te he dado las cosas que no pediste, riquezas y gloria, de tal manera que entre los reyes ninguno haya como tú en todos tus días.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 14, 'Y si anduvieres en mis caminos, guardando mis estatutos y mis mandamientos, como anduvo David tu padre, yo alargaré tus días.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 15, 'Cuando Salomón despertó, vio que era sueño; y vino a Jerusalén, y se presentó delante del arca del pacto de Jehová, y sacrificó holocaustos y ofreció sacrificios de paz, e hizo también banquete a todos sus siervos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 16, 'Sabiduría y prosperidad de Salomón. En aquel tiempo vinieron al rey dos mujeres rameras, y se presentaron delante de él.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 17, 'Y dijo una de ellas: ¡Ah, señor mío! Yo y esta mujer morábamos en una misma casa, y yo di a luz estando con ella en la casa.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 18, 'Aconteció al tercer día después de dar yo a luz, que ésta dio a luz también, y morábamos nosotras juntas; ninguno de fuera estaba en casa, sino nosotras dos en la casa.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 19, 'Y una noche el hijo de esta mujer murió, porque ella se acostó sobre él.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 20, 'Y se levantó a medianoche y tomó a mi hijo de junto a mí, estando yo tu sierva durmiendo, y lo puso a su lado, y puso al lado mío su hijo muerto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 21, 'Y cuando yo me levanté de madrugada para dar el pecho a mi hijo, he aquí que estaba muerto; pero lo observé por la mañana, y vi que no era mi hijo, el que yo había dado a luz.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 22, 'Entonces la otra mujer dijo: No; mi hijo es el que vive, y tu hijo es el muerto. Y la otra volvió a decir: No; tu hijo es el muerto, y mi hijo es el que vive. Así hablaban delante del rey.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 23, '23 El rey entonces dijo: Esta dice: Mi hijo es el que vive, y tu hijo es el muerto; y la otra dice: No, mas el tuyo es el muerto, y mi hijo es el que vive.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 24, 'Y dijo el rey: Traedme una espada. Y trajeron al rey una espada.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 25, 'En seguida el rey dijo: Partid por medio al niño vivo, y dad la mitad a la una, y la otra mitad a la otra.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 26, 'Entonces la mujer de quien era el hijo vivo, habló al rey (porque sus entrañas se le conmovieron por su hijo), y dijo: ¡Ah, señor mío! dad a ésta el niño vivo, y no lo matéis. Mas la otra dijo: Ni a mí ni a ti; partidlo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 27, 'Entonces el rey respondió y dijo: Dad a aquélla el hijo vivo, y no lo matéis; ella es su madre.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 11, 3, 28, 'Y todo Israel oyó aquel juicio que había dado el rey; y temieron al rey, porque vieron que había en él sabiduría de Dios para juzgar.');
