Insert into Idiomas (Nombre) values ('Español');
Insert Into Versiones(NombreVersion, SiglasVersion, Traductor, FechaVersion, Id_Idioma) Values ('Reina Valera 1960', 'RV60', 'Humberto Gómez Caballero', '1569-09-28', 1)
Insert Into Testamentos(Nombre, OrdenTest, Id_Idioma) Values ('Antiguo Testamento', 1, 1)
Insert Into Libros(Nombre, OrdenLibro, CapitulosTot, Autor, Año, Id_Testamento, Id_Idioma) Values ('Exodo', 1, 40, 'Moises', 1569, 1, 1)
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 1, 'Estos son los nombres de los hijos de Israel que entraron en Egipto con Jacob; cada uno entró con su familia')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 2, 'Rubén, Simeón, Leví, Judá,')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 3, 'Isacar, Zabulón, Benjamín,')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 4, 'Dan, Neftalí, Gad y Aser.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 5, 'Todas las personas que le nacieron a Jacob fueron setenta. Y José estaba en Egipto.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 6, 'Y murió José, y todos sus hermanos, y toda aquella generación.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 7, 'Y los hijos de Israel fructificaron y se multiplicaron, y fueron aumentados y fortalecidos en extremo, y se llenó de ellos la tierra.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 8, 'Entretanto, se levantó sobre Egipto un nuevo rey que no conocía a José; y dijo a su pueblo:')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 9, 'He aquí, el pueblo de los hijos de Israel es mayor y más fuerte que nosotros.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 10, 'Ahora, pues, seamos sabios para con él, para que no se multiplique, y acontezca que viniendo guerra, él también se una a nuestros enemigos y pelee contra nosotros, y se vaya de la tierra.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 11, 'Entonces pusieron sobre ellos comisarios de tributos que los molestasen con sus cargas; y edificaron para Faraón las ciudades de almacenaje, Pitón y Ramesés.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 12, 'Pero cuanto más los oprimían, tanto más se multiplicaban y crecían, de manera que los egipcios temían a los hijos de Israel.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 13, 'Y los egipcios hicieron servir a los hijos de Israel con dureza,')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 14, 'y amargaron su vida con dura servidumbre, en hacer barro y ladrillo, y en toda labor del campo y en todo su servicio, al cual los obligaban con rigor.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 15, 'Y habló el rey de Egipto a las parteras de las hebreas, una de las cuales se llamaba Sifra, y otra Fúa, y les dijo:')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 16, 'Cuando asistáis a las hebreas en sus partos, y veáis el sexo, si es hijo, matadlo; y si es hija, entonces viva.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 17, 'Pero las parteras temieron a Dios, y no hicieron como les mandó el rey de Egipto, sino que preservaron la vida a los niños.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 18, 'Y el rey de Egipto hizo llamar a las parteras y les dijo: ¿Por qué habéis hecho esto, que habéis preservado la vida a los niños?')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 19, 'Y las parteras respondieron a Faraón: Porque las mujeres hebreas no son como las egipcias; pues son robustas, y dan a luz antes que la partera venga a ellas.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 20, 'Y Dios hizo bien a las parteras; y el pueblo se multiplicó y se fortaleció en gran manera.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 21, 'Y por haber las parteras temido a Dios, él prosperó sus familias.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 1, 22, 'Entonces Faraón mandó a todo su pueblo, diciendo: Echad al río a todo hijo que nazca, y a toda hija preservad la vida.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 1, 'Un varón de la familia de Leví fue y tomó por mujer a una hija de Leví,')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 2, 'la que concibió, y dio a luz un hijo; y viéndole que era hermoso, le tuvo escondido tres meses.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 3, 'Pero no pudiendo ocultarle más tiempo, tomó una arquilla de juncos y la calafateó con asfalto y brea, y colocó en ella al niño y lo puso en un carrizal a la orilla del río.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 4, 'Y una hermana suya se puso a lo lejos, para ver lo que le acontecería.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 5, 'Y la hija de Faraón descendió a lavarse al río, y paseándose sus doncellas por la ribera del río, vio ella la arquilla en el carrizal, y envió una criada suya a que la tomase.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 6, 'Y cuando la abrió, vio al niño; y he aquí que el niño lloraba. Y teniendo compasión de él, dijo: De los niños de los hebreos es éste.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 7, 'Entonces su hermana dijo a la hija de Faraón: ¿Iré a llamarte una nodriza de las hebreas, para que te críe este niño?')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 8, 'Y la hija de Faraón respondió: Ve. Entonces fue la doncella, y llamó a la madre del niño,')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 9, 'a la cual dijo la hija de Faraón: Lleva a este niño y críamelo, y yo te lo pagaré. Y la mujer tomó al niño y lo crió.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 10, 'Y cuando el niño creció, ella lo trajo a la hija de Faraón, la cual lo prohijó, y le puso por nombre Moisés, diciendo: Porque de las aguas lo saqué.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 11, 'En aquellos días sucedió que crecido ya Moisés, salió a sus hermanos, y los vio en sus duras tareas, y observó a un egipcio que golpeaba a uno de los hebreos, sus hermanos.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 12, 'Entonces miró a todas partes, y viendo que no parecía nadie, mató al egipcio y lo escondió en la arena.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 13, 'Al día siguiente salió y vio a dos hebreos que reñían; entonces dijo al que maltrataba al otro: ¿Por qué golpeas a tu prójimo?')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 14, 'Y él respondió: ¿Quién te ha puesto a ti por príncipe y juez sobre nosotros? ¿Piensas matarme como mataste al egipcio? Entonces Moisés tuvo miedo, y dijo: Ciertamente esto ha sido descubierto.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 15, 'Oyendo Faraón acerca de este hecho, procuró matar a Moisés; p8ero Moisés huyó de delante de Faraón, y habitó en la tierra de Madián.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 16, 'Y estando sentado junto al pozo, siete hijas que tenía el sacerdote de Madián vinieron a sacar agua para llenar las pilas y dar de beber a las ovejas de su padre.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 17, 'Mas los pastores vinieron y las echaron de allí; entonces Moisés se levantó y las defendió, y dio de beber a sus ovejas.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 18, 'Y volviendo ellas a Reuel su padre, él les dijo: ¿Por qué habéis venido hoy tan pronto?')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 19, 'Ellas respondieron: Un varón egipcio nos defendió de mano de los pastores, y también nos sacó el agua, y dio de beber a las ovejas.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 20, 'Y dijo a sus hijas: ¿Dónde está? ¿Por qué habéis dejado a ese hombre? Llamadle para que coma.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 21, 'Y Moisés convino en morar con aquel varón; y él dio su hija Séfora por mujer a Moisés.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 22, 'Y ella le dio a luz un hijo; y él le puso por nombre Gersón, porque dijo: Forastero soy en tierra ajena.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 23, 'Aconteció que después de muchos días murió el rey de Egipto, y los hijos de Israel gemían a causa de la servidumbre, y clamaron; y subió a Dios el clamor de ellos con motivo de su servidumbre.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 24, 'Y oyó Dios el gemido de ellos, y se acordó de su pacto con Abraham, Isaac y Jacob.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 2, 25, 'Y miró Dios a los hijos de Israel, y los reconoció Dios.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 1, 'Apacentando Moisés las ovejas de Jetro su suegro, sacerdote de Madián, llevó las ovejas a través del desierto, y llegó hasta Horeb, monte de Dios.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 2, 'Y se le apareció el Angel de Jehová en una llama de fuego en medio de una zarza; y él miró, y vio que la zarza ardía en fuego, y la zarza no se consumía.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 3, 'Entonces Moisés dijo: Iré yo ahora y veré esta grande visión, por qué causa la zarza no se quema.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 4, 'Viendo Jehová que él iba a ver, lo llamó Dios de en medio de la zarza, y dijo: ¡Moisés, Moisés! Y él respondió: Heme aquí.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 5, 'Y dijo: No te acerques; quita tu calzado de tus pies, porque el lugar en que tú estás, tierra santa es.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 6, 'Y dijo: Yo soy el Dios de tu padre, Dios de Abraham, Dios de Isaac, y Dios de Jacob. Entonces Moisés cubrió su rostro, porque tuvo miedo de mirar a Dios.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 7, 'Dijo luego Jehová: Bien he visto la aflicción de mi pueblo que está en Egipto, y he oído su clamor a causa de sus exactores; pues he conocido sus angustias,')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 8, 'y he descendido para librarlos de mano de los egipcios, y sacarlos de aquella tierra a una tierra buena y ancha, a tierra que fluye leche y miel, a los lugares del cananeo, del heteo, del amorreo, del ferezeo, del heveo y del jebuseo.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 9, 'El clamor, pues, de los hijos de Israel ha venido delante de mí, y también he visto la opresión con que los egipcios los oprimen.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 10, 'Ven, por tanto, ahora, y te enviaré a Faraón, para que saques de Egipto a mi pueblo, los hijos de Israel.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 11, 'Entonces Moisés respondió a Dios: ¿Quién soy yo para que vaya a Faraón, y saque de Egipto a los hijos de Israel?')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 12, 'Y él respondió: Ve, porque yo estaré contigo; y esto te será por señal de que yo te he enviado: cuando hayas sacado de Egipto al pueblo, serviréis a Dios sobre este monte.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 13, 'Dijo Moisés a Dios: He aquí que llego yo a los hijos de Israel, y les digo: El Dios de vuestros padres me ha enviado a vosotros. Si ellos me preguntaren: ¿Cuál es su nombre?, ¿qué les responderé?')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 14, 'Y respondió Dios a Moisés: YO SOY EL QUE SOY. Y dijo: Así dirás a los hijos de Israel: YO SOY me envió a vosotros.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 15, 'Además dijo Dios a Moisés: Así dirás a los hijos de Israel: Jehová, el Dios de vuestros padres, el Dios de Abraham, Dios de Isaac y Dios de Jacob, me ha enviado a vosotros. Este es mi nombre para siempre; con él se me recordará por todos los siglos.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 16, 'Ve, y reúne a los ancianos de Israel, y diles: Jehová, el Dios de vuestros padres, el Dios de Abraham, de Isaac y de Jacob, me apareció diciendo: En verdad os he visitado, y he visto lo que se os hace en Egipto;')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 17, 'y he dicho: Yo os sacaré de la aflicción de Egipto a la tierra del cananeo, del heteo, del amorreo, del ferezeo, del heveo y del jebuseo, a una tierra que fluye leche y miel.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 18, 'Y oirán tu voz; e irás tú, y los ancianos de Israel, al rey de Egipto, y le diréis: Jehová el Dios de los hebreos nos ha encontrado; por tanto, nosotros iremos ahora camino de tres días por el desierto, para que ofrezcamos sacrificios a Jehová nuestro Dios.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 19, 'Mas yo sé que el rey de Egipto no os dejará ir sino por mano fuerte.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 20, 'Pero yo extenderé mi mano, y heriré a Egipto con todas mis maravillas que haré en él, y entonces os dejará ir.')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 21, 'Y yo daré a este pueblo gracia en los ojos de los egipcios, para que cuando salgáis, no vayáis con las manos vacías;')
Insert Into Versiculos(Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 2, 3, 22, 'sino que pedirá cada mujer a su vecina y a su huéspeda alhajas de plata, alhajas de oro, y vestidos, los cuales pondréis sobre vuestros hijos y vuestras hijas; y despojaréis a Egipto.')
