insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 1, 'En el principio Dios creó los cielos y la tierra. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 2, 'Y la tierra estaba desordenada y vacía, y las tinieblas estaban sobre la faz del abismo, y el Espíritu de Dios se movía sobre la faz de las aguas. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 3, 'Y dijo Dios: Sea la luz; y fue la luz. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 4, 'Y vio Dios que la luz era buena; y separó Dios la luz de las tinieblas. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 5, 'Y llamó Dios a la luz Día, y a las tinieblas llamó Noche. Y fue la tarde y la mañana un día. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 6, 'Luego dijo Dios: Haya expansión en medio de las aguas, y separe las aguas de las aguas. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 7, 'E hizo Dios la expansión, y separó las aguas que estaban debajo de la expansión, de las aguas que estaban sobre la expansión. Y fue así. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 8, 'Y llamó Dios a la expansión Cielos. Y fue la tarde y la mañana el día segundo. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 9, 'Dijo también Dios: Júntense las aguas que están debajo de los cielos en un lugar, y descúbrase lo seco. Y fue así. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 10, 'Y llamó Dios a lo seco Tierra, y a la reunión de las aguas llamó Mares. Y vio Dios que era bueno. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 11, 'Después dijo Dios: Produzca la tierra hierba verde, hierba que dé semilla; árbol de fruto que dé fruto según su género, que su semilla esté en él, sobre la tierra. Y fue así. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 12, 'Produjo, pues, la tierra hierba verde, hierba que da semilla según su naturaleza, y árbol que da fruto, cuya semilla está en él, según su género. Y vio Dios que era bueno. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 13, 'Y fue la tarde y la mañana el día tercero. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 14, 'Dijo luego Dios: Haya lumbreras en la expansión de los cielos para separar el día de la noche; y sirvan de señales para las estaciones, para días y años, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 15, 'y sean por lumbreras en la expansión de los cielos para alumbrar sobre la tierra. Y fue así. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 16, 'E hizo Dios las dos grandes lumbreras; la lumbrera mayor para que señorease en el día, y la lumbrera menor para que señorease en la noche; hizo también las estrellas. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 17, 'Y las puso Dios en la expansión de los cielos para alumbrar sobre la tierra, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 18, 'y para señorear en el día y en la noche, y para separar la luz de las tinieblas. Y vio Dios que era bueno. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 19, 'Y fue la tarde y la mañana el día cuarto. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 20, 'Dijo Dios: Produzcan las aguas seres vivientes, y aves que vuelen sobre la tierra, en la abierta expansión de los cielos. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 21, 'Y creó Dios los grandes monstruos marinos, y todo ser viviente que se mueve, que las aguas produjeron según su género, y toda ave alada según su especie. Y vio Dios que era bueno. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 22, 'Y Dios los bendijo, diciendo: Fructificad y multiplicaos, y llenad las aguas en los mares, y multiplíquense las aves en la tierra. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 23, 'Y fue la tarde y la mañana el día quinto. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 24, 'Luego dijo Dios: Produzca la tierra seres vivientes según su género, bestias y serpientes y animales de la tierra según su especie. Y fue así. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 25, 'E hizo Dios animales de la tierra según su género, y ganado según su género, y todo animal que se arrastra sobre la tierra según su especie. Y vio Dios que era bueno. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 26, 'Entonces dijo Dios: Hagamos al hombre a nuestra imagen, conforme a nuestra semejanza; y señoree en los peces del mar, en las aves de los cielos, en las bestias, en toda la tierra, y en todo animal que se arrastra sobre la tierra. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 27, 'Y creó Dios al hombre a su imagen, a imagen de Dios lo creó; varón y hembra los creó. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 28, 'Y los bendijo Dios, y les dijo: Fructificad y multiplicaos; llenad la tierra, y sojuzgadla, y señoread en los peces del mar, en las aves de los cielos, y en todas las bestias que se mueven sobre la tierra. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 29, 'Y dijo Dios: He aquí que os he dado toda planta que da semilla, que está sobre toda la tierra, y todo árbol en que hay fruto y que da semilla; os serán para comer. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 30, 'Y a toda bestia de la tierra, y a todas las aves de los cielos, y a todo lo que se arrastra sobre la tierra, en que hay vida, toda planta verde les será para comer. Y fue así. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 31, 'Y vio Dios todo lo que había hecho, y he aquí que era bueno en gran manera. Y fue la tarde y la mañana el día sexto. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 1, 'Fueron, pues, acabados los cielos y la tierra, y todo el ejército de ellos. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 2, 'Y acabó Dios en el día séptimo la obra que hizo; y reposó el día séptimo de toda la obra que hizo. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 3, 'Y bendijo Dios al día séptimo, y lo santificó, porque en él reposó de toda la obra que había hecho en la creación. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 4, 'Estos son los orígenes de los cielos y de la tierra cuando fueron creados, el día que Jehová Dios hizo la tierra y los cielos, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 5, 'y toda planta del campo antes que fuese en la tierra, y toda hierba del campo antes que naciese; porque Jehová Dios aún no había hecho llover sobre la tierra, ni había hombre para que labrase la tierra, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 6, 'sino que subía de la tierra un vapor, el cual regaba toda la faz de la tierra. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 7, 'Entonces Jehová Dios formó al hombre del polvo de la tierra, y sopló en su nariz aliento de vida, y fue el hombre un ser viviente. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 8, 'Y Jehová Dios plantó un huerto en Edén, al oriente; y puso allí al hombre que había formado. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 9, 'Y Jehová Dios hizo nacer de la tierra todo árbol delicioso a la vista, y bueno para comer; también el árbol de vida en medio del huerto, y el árbol de la ciencia del bien y del mal. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 10, 'Y salía de Edén un río para regar el huerto, y de allí se repartía en cuatro brazos. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 11, 'El nombre del uno era Pisón; éste es el que rodea toda la tierra de Havila, donde hay oro; ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 12, 'y el oro de aquella tierra es bueno; hay allí también bedelio y ónice. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 13, 'El nombre del segundo río es Gihón; éste es el que rodea toda la tierra de Cus. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 14, 'Y el nombre del tercer río es Hidekel; éste es el que va al oriente de Asiria. Y el cuarto río es el Eufrates. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 15, 'Tomó, pues, Jehová Dios al hombre, y lo puso en el huerto de Edén, para que lo labrara y lo guardase. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 16, 'Y mandó Jehová Dios al hombre, diciendo: De todo árbol del huerto podrás comer; ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 17, 'mas del árbol de la ciencia del bien y del mal no comerás; porque el día que de él comieres, ciertamente morirás. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 18, 'Y dijo Jehová Dios: No es bueno que el hombre esté solo; le haré ayuda idónea para él. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 19, 'Jehová Dios formó, pues, de la tierra toda bestia del campo, y toda ave de los cielos, y las trajo a Adán para que viese cómo las había de llamar; y todo lo que Adán llamó a los animales vivientes, ese es su nombre. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 20, 'Y puso Adán nombre a toda bestia y ave de los cielos y a todo ganado del campo; mas para Adán no se halló ayuda idónea para él. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 21, 'Entonces Jehová Dios hizo caer sueño profundo sobre Adán, y mientras éste dormía, tomó una de sus costillas, y cerró la carne en su lugar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 22, 'Y de la costilla que Jehová Dios tomó del hombre, hizo una mujer, y la trajo al hombre. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 23, 'Dijo entonces Adán: Esto es ahora hueso de mis huesos y carne de mi carne; ésta será llamada Varona, porque del varón fue tomada. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 24, 'Por tanto, dejará el hombre a su padre y a su madre, y se unirá a su mujer, y serán una sola carne. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 25, 'Y estaban ambos desnudos, Adán y su mujer, y no se avergonzaban. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 1, 'Pero la serpiente era astuta, más que todos los animales del campo que Jehová Dios había hecho; la cual dijo a la mujer: ¿Conque Dios os ha dicho: No comáis de todo árbol del huerto?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 2, 'Y la mujer respondió a la serpiente: Del fruto de los árboles del huerto podemos comer; ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 3, 'pero del fruto del árbol que está en medio del huerto dijo Dios: No comeréis de él, ni le tocaréis, para que no muráis. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 4, 'Entonces la serpiente dijo a la mujer: No moriréis; ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 5, 'sino que sabe Dios que el día que comáis de él, serán abiertos vuestros ojos, y seréis como Dios, sabiendo el bien y el mal.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 6, 'Y vio la mujer que el árbol era bueno para comer, y que era agradable a los ojos, y árbol codiciable para alcanzar la sabiduría; y tomó de su fruto, y comió; y dio también a su marido, el cual comió así como ella. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 7, 'Entonces fueron abiertos los ojos de ambos, y conocieron que estaban desnudos; entonces cosieron hojas de higuera, y se hicieron delantales. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 8, 'Y oyeron la voz de Jehová Dios que se paseaba en el huerto, al aire del día; y el hombre y su mujer se escondieron de la presencia de Jehová Dios entre los árboles del huerto. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 9, 'Mas Jehová Dios llamó al hombre, y le dijo: ¿Dónde estás tú? ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 10, 'Y él respondió: Oí tu voz en el huerto, y tuve miedo, porque estaba desnudo; y me escondí. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 11, 'Y Dios le dijo: ¿Quién te enseñó que estabas desnudo? ¿Has comido del árbol de que yo te mandé no comieses? ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 12, 'Y el hombre respondió: La mujer que me diste por compañera me dio del árbol, y yo comí. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 13, 'Entonces Jehová Dios dijo a la mujer: ¿Qué es lo que has hecho? Y dijo la mujer: La serpiente me engañó, y comí. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 14, 'Y Jehová Dios dijo a la serpiente: Por cuanto esto hiciste, maldita serás entre todas las bestias y entre todos los animales del campo; sobre tu pecho andarás, y polvo comerás todos los días de tu vida. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 15, 'Y pondré enemistad entre ti y la mujer, y entre tu simiente y la simiente suya; ésta te herirá en la cabeza, y tú le herirás en el calcañar. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 16, 'A la mujer dijo: Multiplicaré en gran manera los dolores en tus preñeces; con dolor darás a luz los hijos; y tu deseo será para tu marido, y él se enseñoreará de ti. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 17, 'Y al hombre dijo: Por cuanto obedeciste a la voz de tu mujer, y comiste del árbol de que te mandé diciendo: No comerás de él; maldita será la tierra por tu causa; con dolor comerás de ella todos los días de tu vida. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 18, 'Espinos y cardos te producirá, y comerás plantas del campo. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 19, 'Con el sudor de tu rostro comerás el pan hasta que vuelvas a la tierra, porque de ella fuiste tomado; pues polvo eres, y al polvo volverás.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 20, 'Y llamó Adán el nombre de su mujer, Eva, por cuanto ella era madre de todos los vivientes. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 21, 'Y Jehová Dios hizo al hombre y a su mujer túnicas de pieles, y los vistió. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 22, 'Y dijo Jehová Dios: He aquí el hombre es como uno de nosotros, sabiendo el bien y el mal; ahora, pues, que no alargue su mano, y tome también del árbol de la vida, y coma, y viva para siempre. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 23, 'Y lo sacó Jehová del huerto del Edén, para que labrase la tierra de que fue tomado.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 24, 'Echó, pues, fuera al hombre, y puso al oriente del huerto de Edén querubines, y una espada encendida que se revolvía por todos lados, para guardar el camino del árbol de la vida. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 1, 'Aconteció en los días de Amrafel rey de Sinar, Arioc rey de Elasar, Quedorlaomer rey de Elam, y Tidal rey de Goim.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 2, 'Que éstos hicieron guerra contra Bera rey de Sodoma, contra Birsa rey de Gomorra, contra Sinab rey de Adma, contra Semeber rey de Zeboim, y contra el rey de Bela, la cual es Zoar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 3, 'Todos éstos se juntaron en el valle de Sidim, que es el Mar Salado.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 4, 'Doce años habían servido a Quedorlaomer, y en el decimotercero se rebelaron.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 5, 'Y en el año decimocuarto vino Quedorlaomer, y los reyes que estaban de su parte, y derrotaron a los refaítas en Astarot Karnaim, a los zuzitas en Ham, a los emitas en Save-quiriataim.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 6, 'Y a los horeos en el monte de Seir, hasta la llanura de Parán, que está junto al desierto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 7, 'Y volvieron y vinieron a En-mispat, que es Cades, y devastaron todo el país de los amalecitas, y también al amorreo que habitaba en Hazezontamar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 8, 'Y salieron el rey de Sodoma, el rey de Gomorra, el rey de Adma, el rey de Zeboim y el rey de Bela, que es Zoar, y ordenaron contra ellos batalla en el valle de Sidim.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 9, 'Esto es, contra Quedorlaomer rey de Elam, Tidal rey de Goim, Amrafel rey de Sinar, y Arioc rey de Elasar; cuatro reyes contra cinco.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 10, 'Y el valle de Sidim estaba lleno de pozos de asfalto; y cuando huyeron el rey de Sodoma y el de Gomorra, algunos cayeron allí; y los demás huyeron al monte.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 11, 'Y tomaron toda la riqueza de Sodoma y de Gomorra, y todas sus provisiones, y se fueron.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 12, 'Tomaron también a Lot, hijo del hermano de Abram, que moraba en Sodoma, y sus bienes, y se fueron.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 13, 'Y vino uno de los que escaparon, y lo anunció a Abram el hebreo, que habitaba en el encinar de Mamre el amorreo, hermano de Escol y hermano de Aner, los cuales eran aliados de Abram.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 14, 'Oyó Abram que su pariente estaba prisionero, y armó a sus criados, los nacidos en su casa, trescientos dieciocho, y los siguió hasta Dan.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 15, 'Y cayó sobre ellos de noche, él y sus siervos, y les atacó, y les fue siguiendo hasta Hoba al norte de Damasco.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 16, 'Y recobró todos los bienes, y también a Lot su pariente y sus bienes, y a las mujeres y demás gente.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 17, 'Cuando volvía de la derrota de Quedorlaomer y de los reyes que con él estaban, salió el rey de Sodoma a recibirlo al valle de Save, que es el Valle del Rey');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 18, 'Entonces Melquisedec, rey de Salem y sacerdote del Dios Altísimo, sacó pan y vino.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 19, 'Y le bendijo, diciendo: Bendito sea Abram del Dios Altísimo, creador de los cielos y de la tierra.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 20, 'Y bendito sea el Dios Altísimo, que entregó tus enemigos en tu mano. Y le dio Abram los diezmos de todo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 21, 'Entonces el rey de Sodoma dijo a Abram: Dame las personas, y toma para ti los bienes.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 22, 'Y respondió Abram al rey de Sodoma: He alzado mi mano a Jehová Dios Altísimo, creador de los cielos y de la tierra.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 23, 'Que desde un hilo hasta una correa de calzado, nada tomaré de todo lo que es tuyo, para que no digas: Yo enriquecí a Abram.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 14, 24, 'Excepto solamente lo que comieron los jóvenes, y la parte de los varones que fueron conmigo, Aner, Escol y Mamre, los cuales tomarán su parte.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 1, 'Después de estas cosas vino la palabra de Jehová a Abram en visión, diciendo: No temas, Abram; yo soy tu escudo, y tu galardón será sobremanera grande.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 2, 'Y respondió Abram: Señor Jehová, ¿qué me darás, siendo así que ando sin hijo, y el mayordomo de mi casa es ese damasceno Eliezer?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 3, 'Dijo también Abram: Mira que no me has dado prole, y he aquí que será mi heredero un esclavo nacido en mi casa.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 4, 'Luego vino a él palabra de Jehová, diciendo: No te heredará éste, sino un hijo tuyo será el que te heredará.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 5, 'Y lo llevó fuera, y le dijo: Mira ahora los cielos, y cuenta las estrellas, si las puedes contar. Y le dijo: Así será tu descendencia.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 6, 'Y creyó a Jehová, y le fue contado por justicia.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 7, 'Y le dijo: Yo soy Jehová, que te saqué de Ur de los caldeos, para darte a heredar esta tierra.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 8, 'Y él respondió: Señor Jehová, ¿en qué conoceré que la he de heredar?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 9, 'Y le dijo: Tráeme una becerra de tres años, y una cabra de tres años, y un carnero de tres años, una tórtola también, y un palomino.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 10, 'Y tomó él todo esto, y los partió por la mitad, y puso cada mitad una enfrente de la otra; mas no partió las aves.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 11, 'Y descendían aves de rapiña sobre los cuerpos muertos, y Abram las ahuyentaba.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 12, 'Mas a la caída del sol sobrecogió el sueño a Abram, y he aquí que el temor de una grande oscuridad cayó sobre él.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 13, 'Entonces Jehová dijo a Abram: Ten por cierto que tu descendencia morará en tierra ajena, y será esclava allí, y será oprimida cuatrocientos años.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 14, 'Mas también a la nación a la cual servirán, juzgaré yo; y después de esto saldrán con gran riqueza.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 15, 'Y tú vendrás a tus padres en paz, y serás sepultado en buena vejez.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 16, 'Y en la cuarta generación volverán acá; porque aún no ha llegado a su colmo la maldad del amorreo hasta aquí.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 17, 'Y sucedió que puesto el sol, y ya oscurecido, se veía un horno humeando, y una antorcha de fuego que pasaba por entre los animales divididos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 18, 'En aquel día hizo Jehová un pacto con Abram, diciendo: A tu descendencia daré esta tierra, desde el río de Egipto hasta el río grande, el río Eufrates.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 19, 'La tierra de los ceneos, los cenezeos, los admoneos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 20, 'Los heteos, los ferezeos, los refaítas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 15, 21, 'Los amorreos, los cananeos, los gergeseos y los jebuseos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 16, 1, 'Sarai mujer de Abram no le daba hijos; y ella tenía una sierva egipcia, que se llamaba Agar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 16, 2, 'Dijo entonces Sarai a Abram: Ya ves que Jehová me ha hecho estéril; te ruego, pues, que te llegues a mi sierva; quizá tendré hijos de ella. Y atendió Abram al ruego de Sarai.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 16, 3, 'Y Sarai mujer de Abram tomó a Agar su sierva egipcia, al cabo de diez años que había habitado Abram en la tierra de Canaán, y la dio por mujer a Abram su marido.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 16, 4, 'Y él se llegó a Agar, la cual concibió; y cuando vio que había concebido, miraba con desprecio a su señora.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 16, 5, 'Entonces Sarai dijo a Abram: Mi afrenta sea sobre ti; yo te di mi sierva por mujer, y viéndose encinta, me mira con desprecio; juzgue Jehová entre tú y yo.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 16, 6, 'Y respondió Abram a Sarai: He aquí, tu sierva está en tu mano; haz con ella lo que bien te parezca. Y como Sarai la afligía, ella huyó de su presencia.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 16, 7, 'Y la halló el ángel de Jehová junto a una fuente de agua en el desierto, junto a la fuente que está en el camino de Shur.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 16, 8, 'Y le dijo: Agar, sierva de Sarai, ¿de dónde vienes tú, y a dónde vas? Y ella respondió: Huyo de delante de Sarai mi señora');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 16, 9, 'Y le dijo el ángel de Jehová: Vuélvete a tu señora, y ponte sumisa bajo su mano.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 16, 10, 'Le dijo también el ángel de Jehová: Multiplicaré tanto tu descendencia, que no podrá ser contada a causa de la multitud.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 16, 11, 'Además le dijo el ángel de Jehová: He aquí que has concebido, y darás a luz un hijo, y llamarás su nombre Ismael, porque Jehová ha oído tu aflicción.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 16, 12, 'Y él será hombre fiero; su mano será contra todos, y la mano de todos contra él, y delante de todos sus hermanos habitará.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 16, 13, 'Entonces llamó el nombre de Jehová que con ella hablaba: Tú eres Dios que ve; porque dijo: ¿No he visto también aquí al que me ve?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 16, 14, 'Por lo cual llamó al pozo: Pozo del Viviente-que-me-ve. He aquí está entre Cades y Bered.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 16, 15, 'Y Agar dio a luz un hijo a Abram, y llamó Abram el nombre del hijo que le dio Agar, Ismael.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) values (1, 1, 16, 16, 'Era Abram de edad de ochenta y seis años, cuando Agar dio a luz a Ismael.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 1, 1, 'Llamó Jehová a Moisés, y habló con él desde el tabernáculo de reunión, diciendo:');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 1, 2, 'Habla a los hijos de Israel y diles: Cuando alguno de entre vosotros ofrece ofrenda a Jehová, de ganado vacuno u ovejuno haréis vuestra ofrenda. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 1, 3, 'Si su ofrenda fuere holocausto vacuno, macho sin defecto lo ofrecerá; de su voluntad lo ofrecerá a la puerta del tabernáculo de reunión delante de Jehová.' );
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 1, 4, 'Y pondrá su mano sobre la cabeza del holocausto, y será aceptado para expiación suya.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 1, 5, 'Entonces degollará el becerro en la presencia de Jehová; y los sacerdotes hijos de Aarón ofrecerán la sangre, y la rociarán alrededor sobre el altar, el cual está a la puerta del tabernáculo de reunión.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 1, 6, 'Y desollará el holocausto, y lo dividirá en sus piezas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 1, 7, 'Y los hijos del sacerdote Aarón pondrán fuego sobre el altar, y compondrán la leña sobre el fuego.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 1, 8, 'Luego los sacerdotes hijos de Aarón acomodarán las piezas, la cabeza y la grosura de los intestinos, sobre la leña que está sobre el fuego que habrá encima del altar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 1, 9, 'y lavará con agua los intestinos y las piernas, y el sacerdote hará arder todo sobre el altar; holocausto es, ofrenda encendida de olor grato para Jehová. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 1, 10, 'Si su ofrenda para holocausto fuere del rebaño, de las ovejas o de las cabras, macho sin defecto lo ofrecerá.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 1, 11, 'Y lo degollará al lado norte del altar delante de Jehová; y los sacerdotes hijos de Aarón rociarán su sangre sobre el altar alrededor.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 1, 12, 'Lo dividirá en sus piezas, con su cabeza y la grosura de los intestinos; y el sacerdote las acomodará sobre la leña que está sobre el fuego que habrá encima del altar;');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 1, 13, 'y lavará las entrañas y las piernas con agua; y el sacerdote lo ofrecerá todo, y lo hará arder sobre el altar; holocausto es, ofrenda encendida de olor grato para Jehová. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 1, 14, 'Si la ofrenda para Jehová fuere holocausto de aves, presentará su ofrenda de tórtolas, o de palominos.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 1, 15, 'Y el sacerdote la ofrecerá sobre el altar, y le quitará la cabeza, y hará que arda en el altar; y su sangre será exprimida sobre la pared del altar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 1, 16, 'Y le quitará el buche y las plumas, lo cual echará junto al altar, hacia el oriente, en el lugar de las cenizas. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 1, 17, 'Y la henderá por sus alas, pero no la dividirá en dos; y el sacerdote la hará arder sobre el altar, sobre la leña que estará en el fuego; holocausto es, ofrenda encendida de olor grato para Jehová.  ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 2, 1, 'Cuando alguna persona ofreciere oblación a Jehová, su ofrenda será flor de harina, sobre la cual echará aceite, y pondrá sobre ella incienso');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 2, 2, 'y la traerá a los sacerdotes, hijos de Aarón; y de ello tomará el sacerdote su puño lleno de la flor de harina y del aceite, con todo el incienso, y lo hará arder sobre el altar para memorial; ofrenda encendida es, de olor grato a Jehová. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 2, 3, 'Y lo que resta de la ofrenda será de Aarón y de sus hijos; es cosa santísima de las ofrendas que se queman para Jehová.' );
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 2, 4, 'Cuando ofrecieres ofrenda cocida en horno, será de tortas de flor de harina sin levadura amasadas con aceite, y hojaldres sin levadura untadas con aceite. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 2, 5, 'Mas si ofrecieres ofrenda de sartén, será de flor de harina sin levadura, amasada con aceite, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 2, 6, 'la cual partirás en piezas, y echarás sobre ella aceite; es ofrenda. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 2, 7, 'Si ofrecieres ofrenda cocida en cazuela, se hará de flor de harina con aceite. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 2, 8, 'Y traerás a Jehová la ofrenda que se hará de estas cosas, y la presentarás al sacerdote, el cual la llevará al altar. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 2, 9, 'Y tomará el sacerdote de aquella ofrenda lo que sea para su memorial, y lo hará arder sobre el altar; ofrenda encendida de olor grato a Jehová. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 2, 10, 'Y lo que resta de la ofrenda será de Aarón y de sus hijos; es cosa santísima de las ofrendas que se queman para Jehová. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 2, 11, 'Ninguna ofrenda que ofreciereis a Jehová será con levadura; porque de ninguna cosa leuda, ni de ninguna miel, se ha de quemar ofrenda para Jehová. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 2, 12, 'Como ofrenda de primicias las ofreceréis a Jehová; mas no subirán sobre el altar en olor grato. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 2, 13, 'Y sazonarás con sal toda ofrenda que presentes, y no harás que falte jamás de tu ofrenda la sal del pacto de tu Dios; en toda ofrenda tuya ofrecerás sal. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 2, 14, 'Si ofrecieres a Jehová ofrenda de primicias, tostarás al fuego las espigas verdes, y el grano desmenuzado ofrecerás como ofrenda de tus primicias. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 2, 15, 'Y pondrás sobre ella aceite, y pondrás sobre ella incienso; es ofrenda. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 2, 16, 'Y el sacerdote hará arder el memorial de él, parte del grano desmenuzado y del aceite, con todo el incienso; es ofrenda encendida para Jehová. Ofrendas de paz');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 3, 1, 'Si su ofrenda fuere sacrificio de paz, si hubiere de ofrecerla de ganado vacuno, sea macho o hembra, sin defecto la ofrecerá delante de Jehová. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 3, 2, 'Pondrá su mano sobre la cabeza de su ofrenda, y la degollará a la puerta del tabernáculo de reunión; y los sacerdotes hijos de Aarón rociarán su sangre sobre el altar alrededor. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 3, 3, 'Luego ofrecerá del sacrificio de paz, como ofrenda encendida a Jehová, la grosura que cubre los intestinos, y toda la grosura que está sobre las entrañas, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 3, 4, 'y los dos riñones y la grosura que está sobre ellos, y sobre los ijares; y con los riñones quitará la grosura de los intestinos que está sobre el hígado. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 3, 5, 'Y los hijos de Aarón harán arder esto en el altar, sobre el holocausto que estará sobre la leña que habrá encima del fuego; es ofrenda de olor grato para Jehová. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 3, 6, 'Mas si de ovejas fuere su ofrenda para sacrificio de paz a Jehová, sea macho o hembra, la ofrecerá sin defecto. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 3, 7, 'Si ofreciere cordero por su ofrenda, lo ofrecerá delante de Jehová. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 3, 8, 'Pondrá su mano sobre la cabeza de su ofrenda, y después la degollará delante del tabernáculo de reunión; y los hijos de Aarón rociarán su sangre sobre el altar alrededor. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 3, 9, 'Y del sacrificio de paz ofrecerá por ofrenda encendida a Jehová la grosura, la cola entera, la cual quitará a raíz del espinazo, la grosura que cubre todos los intestinos, y toda la que está sobre las entrañas. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 3, 10, 'Asimismo los dos riñones y la grosura que está sobre ellos, y la que está sobre los ijares; y con los riñones quitará la grosura de sobre el hígado. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 3, 11, 'Y el sacerdote hará arder esto sobre el altar; vianda es de ofrenda encendida para Jehová. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 3, 12, 'Si fuere cabra su ofrenda, la ofrecerá delante de Jehová. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 3, 13, 'Pondrá su mano sobre la cabeza de ella, y la degollará delante del tabernáculo de reunión; y los hijos de Aarón rociarán su sangre sobre el altar alrededor. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 3, 14, 'Después ofrecerá de ella su ofrenda encendida a Jehová; la grosura que cubre los intestinos, y toda la grosura que está sobre las entrañas, ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 3, 15, 'los dos riñones, la grosura que está sobre ellos, y la que está sobre los ijares; y con los riñones quitará la grosura de sobre el hígado. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 3, 16, 'Y el sacerdote hará arder esto sobre el altar; vianda es de ofrenda que se quema en olor grato a Jehová; toda la grosura es de Jehová. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 3, 3, 17, 'Estatuto perpetuo será por vuestras edades, dondequiera que habitéis, que ninguna grosura ni ninguna sangre comeréis. Ofrendas por el pecado');
