INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,4,'1 Habló Jehová a Moisés, diciendo: ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,1,'2 Habla a los hijos de Israel y diles: Cuando alguna persona pecare por yerro en alguno de los mandamientos de Jehová sobre cosas que no se han de hacer, e hiciere alguna de ellas;  ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,2,'3 si el sacerdote ungido pecare según el pecado del pueblo, ofrecerá a Jehová, por su pecado que habrá cometido, un becerro sin defecto para expiación. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,3,'4 Traerá el becerro a la puerta del tabernáculo de reunión delante de Jehová, y pondrá su mano sobre la cabeza del becerro, y lo degollará delante de Jehová.: ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,5,'5 Y el sacerdote ungido tomará de la sangre del becerro, y la traerá al tabernáculo de reunión;  ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,6,'6 y mojará el sacerdote su dedo en la sangre, y rociará de aquella sangre siete veces delante de Jehová, hacia el velo del santuario.  ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,7,'7 Y el sacerdote pondrá de esa sangre sobre los cuernos del altar del incienso aromático, que está en el tabernáculo de reunión delante de Jehová; y echará el resto de la sangre del becerro al pie del altar del holocausto, que está a la puerta del tabernáculo de reunión. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,8,'8 Y tomará del becerro para la expiación toda su grosura, la que cubre los intestinos, y la que está sobre las entrañas, ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,9,'9 los dos riñones, la grosura que está sobre ellos, y la que está sobre los ijares; y con los riñones quitará la grosura de sobre el hígado,  ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,10,'10 de la manera que se quita del buey del sacrificio de paz; y el sacerdote la hará arder sobre el altar del holocausto.  ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,11,'11 Y la piel del becerro, y toda su carne, con su cabeza, sus piernas, sus intestinos y su estiércol,  ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,12,'12 en fin, todo el becerro sacará fuera del campamento a un lugar limpio, donde se echan las cenizas, y lo quemará al fuego sobre la leña; en donde se echan las cenizas será quemado.  ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,13,'13 Si toda la congregación de Israel hubiere errado, y el yerro estuviere oculto a los ojos del pueblo, y hubieren hecho algo contra alguno de los mandamientos de Jehová en cosas que no se han de hacer, y fueren culpables;  ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,14,'14 luego que llegue a ser conocido el pecado que cometieren, la congregación ofrecerá un becerro por expiación, y lo traerán delante del tabernáculo de reunión.  ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,15,'15 Y los ancianos de la congregación pondrán sus manos sobre la cabeza del becerro delante de Jehová, y en presencia de Jehová degollarán aquel becerro. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,16,'16 Y el sacerdote ungido meterá de la sangre del becerro en el tabernáculo de reunión,  ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,17,'17 y mojará el sacerdote su dedo en la misma sangre, y rociará siete veces delante de Jehová hacia el velo. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,18,'18 Y de aquella sangre pondrá sobre los cuernos del altar que está delante de Jehová en el tabernáculo de reunión, y derramará el resto de la sangre al pie del altar del holocausto, que está a la puerta del tabernáculo de reunión. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,19,'19 Y le quitará toda la grosura y la hará arder sobre el altar.');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,20,'20 Y hará de aquel becerro como hizo con el becerro de la expiación; lo mismo hará de él; así hará el sacerdote expiación por ellos, y obtendrán perdón. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,21,'21 Y sacará el becerro fuera del campamento, y lo quemará como quemó el primer becerro; expiación es por la congregación. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,22,'22 Cuando pecare un jefe, e hiciere por yerro algo contra alguno de todos los mandamientos de Jehová su Dios sobre cosas que no se han de hacer, y pecare; ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,23,'23 luego que conociere su pecado que cometió, presentará por su ofrenda un macho cabrío sin defecto. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,24,'24 Y pondrá su mano sobre la cabeza del macho cabrío, y lo degollará en el lugar donde se degüella el holocausto, delante de Jehová; es expiación. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,25,'25 Y con su dedo el sacerdote tomará de la sangre de la expiación, y la pondrá sobre los cuernos del altar del holocausto, y derramará el resto de la sangre al pie del altar del holocausto, ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,26,'26 y quemará toda su grosura sobre el altar, como la grosura del sacrificio de paz; así el sacerdote hará por él la expiación de su pecado, y tendrá perdón. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,27,'27 Si alguna persona del pueblo pecare por yerro, haciendo algo contra alguno de los mandamientos de Jehová en cosas que no se han de hacer, y delinquiere;');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,28,'28 luego que conociere su pecado que cometió, traerá por su ofrenda una cabra, una cabra sin defecto, por su pecado que cometió. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,29,'29 Y pondrá su mano sobre la cabeza de la ofrenda de la expiación, y la degollará en el lugar del holocausto. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,30,'30 Luego con su dedo el sacerdote tomará de la sangre, y la pondrá sobre los cuernos del altar del holocausto, y derramará el resto de la sangre al pie del altar. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,31,'31 Y le quitará toda su grosura, de la manera que fue quitada la grosura del sacrificio de paz; y el sacerdote la hará arder sobre el altar en olor grato a Jehová; así hará el sacerdote expiación por él, y será perdonado. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,32,'32 Y si por su ofrenda por el pecado trajere cordero, hembra sin defecto traerá. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,33,'33 Y pondrá su mano sobre la cabeza de la ofrenda de expiación, y la degollará por expiación en el lugar donde se degüella el holocausto. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,34,'34 Después con su dedo el sacerdote tomará de la sangre de la expiación, y la pondrá sobre los cuernos del altar del holocausto, y derramará el resto de la sangre al pie del altar. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,4,35,'35 Y le quitará toda su grosura, como fue quitada la grosura del sacrificio de paz, y el sacerdote la hará arder en el altar sobre la ofrenda encendida a Jehová; y le hará el sacerdote expiación de su pecado que habrá cometido, y será perdonado. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,5,1,'1 Si alguno pecare por haber sido llamado a testificar, y fuere testigo que vio, o supo, y no lo denunciare, él llevará su pecado. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,5,2,'2 Asimismo la persona que hubiere tocado cualquiera cosa inmunda, sea cadáver de bestia inmunda, o cadáver de animal inmundo, o cadáver de reptil inmundo, bien que no lo supiere, será inmunda y habrá delinquido. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,5,3,'3 O si tocare inmundicia de hombre, cualquiera inmundicia suya con que fuere inmundo, y no lo echare de ver, si después llegare a saberlo, será culpable.');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,5,4,'4 O si alguno jurare a la ligera con sus labios hacer mal o hacer bien, en cualquiera cosa que el hombre profiere con juramento, y él no lo entendiere; si después lo entiende, será culpable por cualquiera de estas cosas. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,5,5,'5 Cuando pecare en alguna de estas cosas, confesará aquello en que pecó, ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,5,6,'6 y para su expiación traerá a Jehová por su pecado que cometió, una hembra de los rebaños, una cordera o una cabra como ofrenda de expiación; y el sacerdote le hará expiación por su pecado. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,5,7,'7 Y si no tuviere lo suficiente para un cordero, traerá a Jehová en expiación por su pecado que cometió, dos tórtolas o dos palominos, el uno para expiación, y el otro para holocausto.');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,5,8,'8 Y los traerá al sacerdote, el cual ofrecerá primero el que es para expiación; y le arrancará de su cuello la cabeza, mas no la separará por completo. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,5,9,'9 Y rociará de la sangre de la expiación sobre la pared del altar; y lo que sobrare de la sangre lo exprimirá al pie del altar; es expiación. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,5,10,'10 Y del otro hará holocausto conforme al rito; así el sacerdote hará expiación por el pecado de aquel que lo cometió, y será perdonado. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,5,11,'11 Mas si no tuviere lo suficiente para dos tórtolas, o dos palominos, el que pecó traerá como ofrenda la décima parte de un efa de flor de harina para expiación.No pondrá sobre ella aceite, ni sobre ella pondrá incienso, porque es expiación.  ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,5,12,'12 La traerá, pues, al sacerdote, y el sacerdote tomará de ella su puño lleno, para memoria de él, y la hará arder en el altar sobre las ofrendas encendidas a Jehová; es expiación. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,5,13,'13 Y hará el sacerdote expiación por él en cuanto al pecado que cometió en alguna de estas cosas, y será perdonado; y el sobrante será del sacerdote, como la ofrenda de vianda. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,5,14,'Ofrendas expiatorias 14 Habló más Jehová a Moisés, diciendo: ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,5,15,'15 Cuando alguna persona cometiere falta, y pecare por yerro en las cosas santas de Jehová, traerá por su culpa a Jehová un carnero sin defecto de los rebaños, conforme a tu estimación en siclos de plata del siclo del santuario, en ofrenda por el pecado.');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,5,16,'16 Y pagará lo que hubiere defraudado de las cosas santas, y añadirá a ello la quinta parte, y lo dará al sacerdote; y el sacerdote hará expiación por él con el carnero del sacrificio por el pecado, y será perdonado. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,5,17,'17 Finalmente, si una persona pecare, o hiciere alguna de todas aquellas cosas que por mandamiento de Jehová no se han de hacer, aun sin hacerlo a sabiendas, es culpable, y llevará su pecado. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,5,18,'18 Traerá, pues, al sacerdote para expiación, según tú lo estimes, un carnero sin defecto de los rebaños; y el sacerdote le hará expiación por el yerro que cometió por ignorancia, y será perdonado.');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,5,19,'19 Es infracción, y ciertamente delinquió contra Jehová. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,1,'1 Habló Jehová a Moisés, diciendo:');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,2,'2 Cuando una persona pecare e hiciere prevaricación contra Jehová, y negare a su prójimo lo encomendado o dejado en su mano, o bien robare o calumniare a su prójimo, ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,3,'3 o habiendo hallado lo perdido después lo negare, y jurare en falso; en alguna de todas aquellas cosas en que suele pecar el hombre, ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,4,'4 entonces, habiendo pecado y ofendido, restituirá aquello que robó, o el daño de la calumnia, o el depósito que se le encomendó, o lo perdido que halló, ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,5,'5 o todo aquello sobre que hubiere jurado falsamente; lo restituirá por entero a aquel a quien pertenece, y añadirá a ello la quinta parte, en el día de su expiación. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,6,'6 Y para expiación de su culpa traerá a Jehová un carnero sin defecto de los rebaños, conforme a tu estimación, y lo dará al sacerdote para la expiación. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,7,'7 Y el sacerdote hará expiación por él delante de Jehová, y obtendrá perdón de cualquiera de todas las cosas en que suele ofender. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,8,'Leyes de los sacrificios 8 Habló aún Jehová a Moisés, diciendo: ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,9,'9 Manda a Aarón y a sus hijos, y diles: Esta es la ley del holocausto: el holocausto estará sobre el fuego encendido sobre el altar toda la noche, hasta la mañana; el fuego del altar arderá en él. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,10,'10 Y el sacerdote se pondrá su vestidura de lino, y vestirá calzoncillos de lino sobre su cuerpo; y cuando el fuego hubiere consumido el holocausto, apartará él las cenizas de sobre el altar, y las pondrá junto al altar. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,11,'11 Después se quitará sus vestiduras y se pondrá otras ropas, y sacará las cenizas fuera del campamento a un lugar limpio. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,12,'12 Y el fuego encendido sobre el altar no se apagará, sino que el sacerdote pondrá en él leña cada mañana, y acomodará el holocausto sobre él, y quemará sobre él las grosuras de los sacrificios de paz. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,13,'13 El fuego arderá continuamente en el altar; no se apagará. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,14,'14 Esta es la ley de la ofrenda: La ofrecerán los hijos de Aarón delante de Jehová ante el altar. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,15,'15 Y tomará de ella un puñado de la flor de harina de la ofrenda, y de su aceite, y todo el incienso que está sobre la ofrenda, y lo hará arder sobre el altar por memorial en olor grato a Jehová. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,16,'16 Y el sobrante de ella lo comerán Aarón y sus hijos; sin levadura se comerá en lugar santo; en el atrio del tabernáculo de reunión lo comerán. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,17,'17 No se cocerá con levadura; la he dado a ellos por su porción de mis ofrendas encendidas; es cosa santísima, como el sacrificio por el pecado, y como el sacrificio por la culpa. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,18,'18 Todos los varones de los hijos de Aarón comerán de ella. Estatuto perpetuo será para vuestras generaciones tocante a las ofrendas encendidas para Jehová; toda cosa que tocare en ellas será santificada. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,19,'19 Habló también Jehová a Moisés, diciendo: ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,20,'20 Esta es la ofrenda de Aarón y de sus hijos, que ofrecerán a Jehová el día que fueren ungidos: la décima parte de un efa de flor de harina, ofrenda perpetua, la mitad a la mañana y la mitad a la tarde. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,21,'21 En sartén se preparará con aceite; frita la traerás, y los pedazos cocidos de la ofrenda ofrecerás en olor grato a Jehová. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,22,' 22 Y el sacerdote que en lugar de Aarón fuere ungido de entre sus hijos, hará igual ofrenda. Es estatuto perpetuo de Jehová; toda ella será quemada.');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,23,'23 Toda ofrenda de sacerdote será enteramente quemada; no se comerá. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,24,'24 Y habló Jehová a Moisés, diciendo: ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,25,'25 Habla a Aarón y a sus hijos, y diles: Esta es la ley del sacrificio expiatorio: en el lugar donde se degüella el holocausto, será degollada la ofrenda por el pecado delante de Jehová; es cosa santísima. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,26,'26 El sacerdote que la ofreciere por el pecado, la comerá; en lugar santo será comida, en el atrio del tabernáculo de reunión.');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,27,'27 Todo lo que tocare su carne, será santificado; y si salpicare su sangre sobre el vestido, lavarás aquello sobre que cayere, en lugar santo. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,28,'28 Y la vasija de barro en que fuere cocida, será quebrada; y si fuere cocida en vasija de bronce, será fregada y lavada con agua. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,29,'29 Todo varón de entre los sacerdotes la comerá; es cosa santísima. ');
INSERT INTO Versiculos(Id_version,Id_libro,NumeroCap,NumeroVers,Texto) VALUES(1,13,6,30,'30 Mas no se comerá ninguna ofrenda de cuya sangre se metiere en el tabernáculo de reunión para hacer expiación en el santuario; al fuego será quemada.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 1, 'Bienaventurado el varón que no anduvo en consejo de malos, 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 2, 'Sino que en la ley de Jehová está su delicia, 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 3, 'Será como árbol plantado junto a corrientes de aguas, 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 4, 'No así los malos, 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 5, 'Por tanto, no se levantarán los malos en el juicio, 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 1, 6, 'Porque Jehová conoce el camino de los justos; 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 1, '¿Por qué se amotinan las gentes, 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 2, 'Se levantarán los reyes de la tierra, 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 3, 'Rompamos sus ligaduras, 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 4, 'El que mora en los cielos se reirá; 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 5, 'Luego hablará a ellos en su furor, 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 6, 'Pero yo he puesto mi rey 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 7, 'Yo publicaré el decreto; 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 8, 'Pídeme, y te daré por herencia las naciones, 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 9, 'Los quebrantarás con vara de hierro; 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 10, 'Ahora, pues, oh reyes, sed prudentes; 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 11, 'Servid a Jehová con temor, 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 2, 12, 'Honrad al Hijo, para que no se enoje, y perezcáis en el camino; 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 1, '¡Oh Jehová, cuánto se han multiplicado mis adversarios! 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 2, 'Muchos son los que dicen de mí: 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 3, 'Mas tú, Jehová, eres escudo alrededor de mí; 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 4, 'Con mi voz clamé a Jehová, 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 5, 'Yo me acosté y dormí, 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 6, 'No temeré a diez millares de gente, 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 7, 'Levántate, Jehová; sálvame, Dios mío; 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 1, 3, 8, 'La salvación es de Jehová; 
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 1, 1, 'Cantar de los cantares, el cual es de Salomón.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 1, 2, '¡Oh, si él me besara con besos de su boca! Porque mejores son tus amores que el vino. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 1, 3, 'A más del olor de tus suaves ungüentos, Tu nombre es como ungüento derramado; Por eso las doncellas te aman. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 1, 4, 'Atráeme; en pos de ti correremos. El rey me ha metido en sus cámaras; Nos gozaremos y alegraremos en ti; Nos acordaremos de tus amores más que del vino; Con razón te aman. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 1, 5, 'Morena soy, oh hijas de Jerusalén, pero codiciable Como las tiendas de Cedar, Como las cortinas de Salomón. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 1, 6, 'No reparéis en que soy morena, Porque el sol me miró. Los hijos de mi madre se airaron contra mí; Me pusieron a guardar las viñas; Y mi viña, que era mía, no guardé.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 1, 7, 'Hazme saber, oh tú a quien ama mi alma, Dónde apacientas, dónde sesteas al mediodía; Pues ¿por qué había de estar yo como errante Junto a los rebaños de tus compañeros?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 1, 8, 'Si tú no lo sabes, oh hermosa entre las mujeres, Ve, sigue las huellas del rebaño, Y apacienta tus cabritas junto a las cabañas de los pastores. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 1, 9, 'A yegua de los carros de Faraón Te he comparado, amiga mía. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 1, 10, 'Hermosas son tus mejillas entre los pendientes, Tu cuello entre los collares.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 1, 11, 'Zarcillos de oro te haremos, Tachonados de plata.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 1, 12, 'Mientras el rey estaba en su reclinatorio, Mi nardo dio su olor. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 1, 13, 'Mi amado es para mí un manojito de mirra, Que reposa entre mis pechos. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 1, 14, 'Racimo de flores de alheña en las viñas de En-gadi Es para mí mi amado.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 1, 15, 'He aquí que tú eres hermosa, amiga mía; He aquí eres bella; tus ojos son como palomas.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 1, 16, 'He aquí que tú eres hermoso, amado mío, y dulce; Nuestro lecho es de flores.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 1, 17, 'Las vigas de nuestra casa son de cedro, Y de ciprés los artesonados.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 2, 1, 'Yo soy la rosa de Sarón, Y el lirio de los valles.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 2, 2, 'Como el lirio entre los espinos, Así es mi amiga entre las doncellas. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 2, 3, 'Como el manzano entre los árboles silvestres, Así es mi amado entre los jóvenes; Bajo la sombra del deseado me senté, Y su fruto fue dulce a mi paladar.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 2, 4, 'Me llevó a la casa del banquete, Y su bandera sobre mí fue amor.  ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 2, 5, 'Sustentadme con pasas, confortadme con manzanas; Porque estoy enferma de amor. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 2, 6, 'Su izquierda esté debajo de mi cabeza, Y su derecha me abrace.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 2, 7, 'Yo os conjuro, oh doncellas de Jerusalén, Por los corzos y por las ciervas del campo, Que no despertéis ni hagáis velar al amor, Hasta que quiera.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 2, 8, '¡La voz de mi amado! He aquí él viene Saltando sobre los montes, Brincando sobre los collados. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 2, 9, 'Mi amado es semejante al corzo, O al cervatillo. Helo aquí, está tras nuestra pared, Mirando por las ventanas, Atisbando por las celosías.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 2, 10, 'Mi amado habló, y me dijo: Levántate, oh amiga mía, hermosa mía, y ven. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 2, 11, 'Porque he aquí ha pasado el invierno, Se ha mudado, la lluvia se fue; ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 2, 12, ' Se han mostrado las flores en la tierra, El tiempo de la canción ha venido, Y en nuestro país se ha oído la voz de la tórtola.  ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 2, 13, 'La higuera ha echado sus higos, Y las vides en cierne dieron olor; Levántate, oh amiga mía, hermosa mía, y ven.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 2, 14, 'Paloma mía, que estás en los agujeros de la peña, en lo escondido de escarpados parajes, Muéstrame tu rostro, hazme oír tu voz; Porque dulce es la voz tuya, y hermoso tu aspecto.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 2, 15, 'Cazadnos las zorras, las zorras pequeñas, que echan a perder las viñas; Porque nuestras viñas están en cierne. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 2, 16, 'Mi amado es mío, y yo suya; El apacienta entre lirios.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 2, 17, 'Hasta que apunte el día, y huyan las sombras, Vuélvete, amado mío; sé semejante al corzo, o como el cervatillo Sobre los montes de Beter. ');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 3, 1, 'Por las noches busqué en mi lecho al que ama mi alma; Lo busqué, y no lo hallé.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 3, 2, 'Y dije: Me levantaré ahora, y rodearé por la ciudad; Por las calles y por las plazas Buscaré al que ama mi alma; Lo busqué, y no lo hallé.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 3, 3, 'Me hallaron los guardas que rondan la ciudad, Y les dije: ¿Habéis visto al que ama mi alma?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 3, 4, 'Apenas hube pasado de ellos un poco, Hallé luego al que ama mi alma; Lo así, y no lo dejé, Hasta que lo metí en casa de mi madre, Y en la cámara de la que me dio a luz.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 3, 5, 'Yo os conjuro, oh doncellas de Jerusalén, Por los corzos y por las ciervas del campo, Que no despertéis ni hagáis velar al amor, Hasta que quiera.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 3, 6, '¿Quién es esta que sube del desierto como columna de humo, Sahumada de mirra y de incienso Y de todo polvo aromático?');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 3, 7, 'He aquí es la litera de Salomón; Sesenta valientes la rodean, De los fuertes de Israel.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 3, 8, 'Todos ellos tienen espadas, diestros en la guerra; Cada uno su espada sobre su muslo, Por los temores de la noche.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 3, 9, 'El rey Salomón se hizo una carroza De madera del Líbano.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 3, 10, 'Hizo sus columnas de plata, Su respaldo de oro, Su asiento de grana, Su interior recamado de amor Por las doncellas de Jerusalén.');
insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 22, 3, 11, 'Salid, oh doncellas de Sion, y ved al rey Salomón Con la corona con que le coronó su madre en el día de su desposorio, Y el día del gozo de su corazón.');
